# 获取时间戳


def get_time():
    url = 'http://api.m.taobao.com/rest/api3.do?api=mtop.common.getTimestamp'
    response = requests.get(url, headers=headers).json()
    t = response['data']['t']
    return t


# 获取app_token
def get_app_token(login_token):
    url = "https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=" + login_token
    response = requests.get(url, headers=headers).json()
    app_token = response['token_info']['app_token']
    # print("app_token获取成功！")
    # print(app_token)
    return app_token

# 推送server酱
def push_wx(sckey, desp=""):
    """
    推送消息到微信
    """
    if sckey == '':
        print("[注意] 未提供sckey，不进行推送！")
    else:
        server_url = f"https://sc.ftqq.com/{sckey}.send"
        params = {
            "text": '小米运动 步数修改',
            "desp": desp
        }
 
        response = requests.get(server_url, params=params)
        json_data = response.json()
 
        if json_data['errno'] == 0:
            print(f"[{now}] 推送成功。")
        else:
            print(f"[{now}] 推送失败：{json_data['errno']}({json_data['errmsg']})")

